import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './normalbikeinfor.css';

const CarSelector = () => {
  const [selectedCar, setSelectedCar] = useState('');
  const [carImages, setCarImages] = useState([]);
  const [selectedCarIndex, setSelectedCarIndex] = useState(null); // To track selected image index

  // Updated car data with mileage for each car
  const carData = {
    royalenfield: [
      { imgSrc: 'data:image/jpeg;base64,', mileage: '25 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQAZXRTE8ik4LfvWB3U2hETvNc84Hzy-XUu7A&s', mileage: '24 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT6tsqGMuOk5pTZAuZgvo1GyXNfYmOT6iW1LQ&s', mileage: '26 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRUvzGpbTNObYf0sbIeshSRRnRgt2l_Jf3CiA&s', mileage: '23 km/l' }
    ],
    r15: [
      { imgSrc: 'data:image/jpeg;base64,', mileage: '20 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRCIulvqaXP0mRIPDhLMpLg_whBK3_Yf5TnUQ&s', mileage: '21 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSOnbCwr570A8RFG_FvWInUnJiidWGaUdw8LQ&s', mileage: '22 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsjii1SREK7upfEpiM6fAZ2-SnQP6qkBFEUg&s', mileage: '19 km/l' }
    ],
    rx100: [
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRzXFed6TbTk-Jz1d7SymPcxmm2isTNZUbOYg&s', mileage: '18 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQbFm3SAme6JV1JWsgTkZ54JAOvsJXrMVlzeg&s', mileage: '17 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS0yUaF6wMsLIJ2gXr7vpzEQO_VTrKVzo9Mig&s', mileage: '20 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSQMUadUOCBhFenyFcpR8XSSTLbynLEOvTgNw&s', mileage: '19 km/l' }
    ],
    apache: [
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRcbumF_yHpZ7QSrt3ydkTrwapGlMXZB7sM1w&s', mileage: '26 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS_f0sZHrIV13Ki8oTfDd2JyGl6-HQT0GbYzw&s', mileage: '25 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSC_x-ZJHlhaYKDvzJithiijwu38I2sBQx2hg&s', mileage: '27 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSz1aVjm4aR-a1R8vNyI47ltheqHFRD4JYlqQ&s', mileage: '28 km/l' }
    ]
  };

  const handleCarSelect = (event) => {
    const selected = event.target.value;
    setSelectedCar(selected);

    if (selected && carData[selected]) {
      setCarImages(carData[selected]);
      setSelectedCarIndex(null); // Reset selected car index when changing car type
    } else {
      setCarImages([]);
    }
  };

  const handleSelectCar = (index) => {
    setSelectedCarIndex(index);
  };

  return (
    <div className='h-screen'> <div style={{ padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
    {/* Bold "Available Car" and dropdown centered */}
    <label htmlFor="car-select" style={{ fontWeight: 'bold', fontSize: '18px', marginBottom: '10px' }}>
      Available Bike:
    </label>
    <select 
      id="car-select" 
      onChange={handleCarSelect} 
      style={{ padding: '10px', marginBottom: '20px', fontSize: '16px', width: '200px' }}
    >
      <option value="">-- Select a Bike --</option>
      <option value="royalenfield">royal-enfield</option>
      <option value="r15">R15 </option>
      <option value="rx100">rx100</option>
      <option value="apache">Apache</option>
    </select>

    {carImages.length > 0 && (
      <div className="car-cards" style={{ display: 'flex', gap: '20px', marginTop: '20px', flexWrap: 'wrap', justifyContent: 'center' }}>
        {carImages.map((car, index) => (
          <div
            className="car-card-container"
            key={index}
            style={{
              width: '300px',
              textAlign: 'center',
              border: '1px solid #ddd',
              borderRadius: '10px',
              padding: '20px',
              backgroundColor: selectedCarIndex === index ? '#e0ffe0' : 'white',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
              transition: 'transform 0.3s',
              display: 'flex', /* Flex layout for each card */
              flexDirection: 'column', /* Stack elements vertically */
              alignItems: 'center' /* Align the button and image in the center */
            }}
          >
            <img
              src={car.imgSrc}
              alt={`Car Image ${index + 1}`}
              style={{ width: '100%', height: '200px', borderRadius: '5px' }}
              className='car-card'
            />
            <div style={{ marginTop: '10px', fontSize: '16px', fontWeight: 'bold' }}>
              Mileage: {car.mileage}
            </div>

            {/* Add Select Car Button aligned below image */}
            <div style={{ display: 'flex', justifyContent: 'center', marginTop: '10px', width: '100%' }}>
              <button
                className="select-btn bg-fuchsia-900"
                style={{
                  padding: '12px',
                  backgroundColor: selectedCarIndex === index ? '#4CAF50' : '', /* Remove inline background color */
                  color: 'white',
                  border: 'none',
                  borderRadius: '5px',
                  cursor: 'pointer',
                  width: '100%', /* Ensure the button takes full width of the card */
                }}
                onClick={() => handleSelectCar(index)}
              >
                {selectedCarIndex === index ? 'Selected' : 'Select Bike'}
              </button>
            </div>
          </div>
        ))}
      </div>
    )}

    {/* Confirmation Button */}
    <Link to="/sevenbikebook">
      <div className="confirm-booking-container text-center" style={{ marginTop: '20px' }}>
        <button className="confirm-your-booking-button" style={{
          padding: '12px 20px',
          backgroundColor: 'fuchsia-900', /* Update background color */
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          fontSize: '16px'
        }}>
          Confirm Your Booking
        </button>
      </div>
    </Link>
  </div></div>
   
  );
};

export default CarSelector;
