import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './FourCarinfor.css';

const CarSelector = () => {
  const [selectedCar, setSelectedCar] = useState('');
  const [carImages, setCarImages] = useState([]);
  const [selectedCarIndex, setSelectedCarIndex] = useState(null); // To track selected image index

  // Updated car data with mileage for each car
  const carData = {
    maruti: [
      { imgSrc: 'data:image/jpeg;base64,', mileage: '45 km/l' },
      { imgSrc: 'data:image/jpeg;base64,', mileage: '41 km/l' },
      { imgSrc: 'data:image/jpeg;base64,', mileage: '45 km/l' },
      { imgSrc: 'data:image/jpeg;base64,', mileage: '43 km/l' }
    ],
    Tata : [
      { imgSrc: 'data:image/jpeg;base64,', mileage: '20 km/l' },
      { imgSrc: 'data:image/jpeg;base64,', mileage: '41 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR043fSOU_81gNcRJEyhQk-dEnsP8ueQZswMw&s', mileage: '42 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS5U1s3xnnIU-9Zd4DFIPFxehy87nio5wfq9Q&s', mileage: '43 km/l' }
    ],
    tatanexon: [
      { imgSrc: 'data:image/jpeg;base64,', mileage: '38 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS5U1s3xnnIU-9Zd4DFIPFxehy87nio5wfq9Q&s', mileage: '38 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR043fSOU_81gNcRJEyhQk-dEnsP8ueQZswMw&s', mileage: '38 km/l' },
      { imgSrc: 'data:image/jpeg;base64,', mileage: '19 km/l' }
    ],
    swift: [
      { imgSrc: 'data:image/jpeg;base64,', mileage: '16 km/l' },
      { imgSrc: 'data:image/jpeg;base64,', mileage: '15 km/l' },
      { imgSrc: 'data:image/jpeg;base64,', mileage: '17 km/l' },
      { imgSrc: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQxVqz-Zm-9cdqNPpuy7yl-pWbZfrke8y2DAQ&s', mileage: '38 km/l' }
    ]
  };

  const handleCarSelect = (event) => {
    const selected = event.target.value;
    setSelectedCar(selected);

    if (selected && carData[selected]) {
      setCarImages(carData[selected]);
      setSelectedCarIndex(null); // Reset selected car index when changing car type
    } else {
      setCarImages([]);
    }
  };

  const handleSelectCar = (index) => {
    setSelectedCarIndex(index);
  };

  return (
    <div className='h-screen'><div style={{ padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
    {/* Bold "Available Car" and dropdown centered */}
    <label htmlFor="car-select" style={{ fontWeight: 'bold', fontSize: '18px', marginBottom: '10px' }}>
      Available Car:
    </label>
    <select 
      id="car-select" 
      onChange={handleCarSelect} 
      style={{ padding: '10px', marginBottom: '20px', fontSize: '16px', width: '200px' }}
    >
      <option value="">-- Select a Car --</option>
      <option value="maruti">Maruti Dzire</option>
      <option value="Tata">Tata</option>
      <option value="tatanexon">Tata nexon</option>
      <option value="swift">Swift</option>
    </select>

    {carImages.length > 0 && (
      <div className="car-cards" style={{ display: 'flex', gap: '20px', marginTop: '20px', flexWrap: 'wrap', justifyContent: 'center' }}>
        {carImages.map((car, index) => (
          <div
            className="car-card-container"
            key={index}
            style={{
              width: '300px',
              textAlign: 'center',
              border: '1px solid #ddd',
              borderRadius: '10px',
              padding: '20px',
              backgroundColor: selectedCarIndex === index ? '#e0ffe0' : 'white',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
              transition: 'transform 0.3s',
              display: 'flex', /* Flex layout for each card */
              flexDirection: 'column', /* Stack elements vertically */
              alignItems: 'center' /* Align the button and image in the center */
            }}
          >
            <img
              src={car.imgSrc}
              alt={`Car Image ${index + 1}`}
              style={{ width: '100%', height: '200px', borderRadius: '5px' }}
              className='car-card'
            />
            <div style={{ marginTop: '10px', fontSize: '16px', fontWeight: 'bold' }}>
              Mileage: {car.mileage}
            </div>

            {/* Add Select Car Button aligned below image */}
            <div style={{ display: 'flex', justifyContent: 'center', marginTop: '10px', width: '100%' }}>
              <button
                className="select-btn bg-fuchsia-900"
                style={{
                  padding: '12px',
                  backgroundColor: selectedCarIndex === index ? '#4CAF50' : '', /* Remove inline background color */
                  color: 'white',
                  border: 'none',
                  borderRadius: '5px',
                  cursor: 'pointer',
                  width: '100%', /* Ensure the button takes full width of the card */
                }}
                onClick={() => handleSelectCar(index)}
              >
                {selectedCarIndex === index ? 'Selected' : 'Select Car'}
              </button>
            </div>
          </div>
        ))}
      </div>
    )}

    {/* Confirmation Button */}
    <Link to="/fivebook">
      <div className="confirm-booking-container text-center" style={{ marginTop: '20px' }}>
        <button className="confirm-your-booking-button" style={{
          padding: '12px 20px',
          backgroundColor: 'fuchsia-900', /* Update background color */
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          fontSize: '16px'
        }}>
          Confirm Your Booking
        </button>
      </div>
    </Link>
  </div></div>
    
  );
};

export default CarSelector;
